package com.chainsys.day4;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 10; i++) {
			System.out.println(i);
			System.out.println(4 * i);
			System.out.println("Value of i:" + i + "\t" + "Product:" + (5 * i));
		}
	}

}
